<script type="text/javascript" src="<?php echo PASTASISTEMA?>js/jquery-1.11.1.min.js"></script>			<script type="text/javascript" src="<?php echo PASTASISTEMA?>js/bootstrap.min.js"></script>
<script type="text/javascript">function reservaSala(sala,hora){	var strSala = $("#Salas").val().split(",");	var qtdSala = (strSala.length - 1);	for(s=0;s<=qtdSala;s++){		$('input:checkbox[name=Horario_'+strSala[s]+']').each(function() {			if(($(this).val()==hora)&&(strSala[s]!=sala)){				if($(this).attr("disabled")){					if($(this).attr("alt")!=1){						$(this).attr("disabled",false);					}					}else{						if($(this).attr("alt")!=1){						$(this).attr("disabled",true);					}				}				};		});	}	}$(document).ready(function(){	$("#frmItem").submit(function(event){		var strSala = $("#Salas").val().split(",");		var qtdSala = (strSala.length - 1);		var arrSala = "";		var arrValor = "";				alert(qtdSala);		for(s=0;s<=qtdSala-1;s++){			arrSala += strSala[s]+",";			var arrS = "";						$('input:checkbox[name=Horario_'+strSala[s]+']').each(function() {				if ($(this).is(':checked')){					arrS += $(this).val()+"-";				}							});			arrValor += arrS+",";		}		if(arrValor!=""){						$.ajax({				type: "POST", 				dataType: "json",				url: "<?php echo PASTASISTEMA?>Conta/Insert_reservas", 				data: "reservas="+arrValor+"&salas="+arrSala, 				success: function(data){					if(data.sucesse==true){						location.href = "<?php echo PASTASISTEMA?>Conta/reservas";					}				}			});		}else{			alert("Selecione a(s) sala(s) e horario para reserva");					}					return false;	});})</script></body></html>